import React, { useState, useEffect, useRef } from "react";

const Second = () => {
  const videoRef = useRef(null);

  const [filterO, setfilter] = useState("");
  const video = document.getElementById("videoCam");
  const canvas = document.getElementById("canvas");
  const [CanvasState, setCanvasState] = useState("none"); //사
  const [CameraState, setCameraState] = useState(""); //사

  useEffect(() => {
    getWebcam((stream) => {
      videoRef.current.srcObject = stream;
    });
  }, []);

  const getWebcam = (callback) => {
    try {
      const constraints = {
        video: true,
        audio: false,
      };
      navigator.mediaDevices.getUserMedia(constraints).then(callback);
    } catch (err) {
      console.log(err);
      return undefined;
    }
  };

  function GoToCamera(target) {
    // 다시 촬영
    const context = canvas.getContext("2d");
    context.scale(-1, 1); // 좌우 반전
    context.translate(-1024, 0); // 좌우 반전
    context.drawImage(video, 0, 0, "1024", "768");
    setCanvasState("none");
    setCameraState("");
    getWebcam((stream) => {
      videoRef.current.srcObject = stream;
    });
  }

  function screenShot(target) {
    // 카메라 촬영
    setCanvasState(""); // 켄버스 켜기
    setCameraState("none"); //비디오 끄기
    const video = document.getElementById("videoCam");
    const canvas = document.getElementById("canvas");
    const context = canvas.getContext("2d");

    context.scale(-1, 1); // 좌우 반전
    context.translate(-1024, 0); // 좌우 반전
    context.drawImage(video, 0, 0, "1024", "768");
    canvas.toBlob((blob) => {
      //캔버스의 이미지를 파일 객체로 만드는 과정
      let file = new File([blob], "fileName.jpg", { type: "image/jpeg" });
      const uploadFile = [file]; //이미지 객체
    }, "image/jpeg");

    console.log(canvas);
    const image = canvas.toDataURL(); // 이미지 저장하는 코드
    const link = document.createElement("a");
    link.href = image;
    link.download = "PaintJS[🎨]";
    link.click();

    const s = videoRef.current.srcObject;
    s.getTracks().forEach((track) => {
      track.stop();
    });
  }

  return (
    <>
      <form
        action="http://http://20.30.169.231:3001/convert"
        method="post"
        encType="multipart/form-data"
      >
        <div
          style={{
            position: "absolute",
            zIndex: "100",
            width: "1024px",
            backgroundColor: "white",
          }}
        >
          <video
            id="videoCam"
            ref={videoRef}
            autoPlay
            style={{
              display: CameraState,
              width: "1024px",
              height: "768px",
              webkitTransform: "rotateY(180deg)",
            }}
          />

          <canvas
            id="canvas"
            width="1024px"
            height="768px"
            style={{ display: CanvasState }}
          ></canvas>
          {CanvasState === "none" ? (
            <div
              onClick={screenShot}
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                width: "70px",
                height: "70px",
                margin: "10px",
                borderRadius: "100px",
                position: "absolute",
                zIndex: "101",
                bottom: "5%",
                left: "46%",
                cursor: "pointer",
                backgroundColor: "white",
              }}
            >
              <div
                style={{
                  textAlign: "center",
                  width: "60px",
                  height: "60px",
                  border: "2px solid",
                  borderRadius: "100px",
                }}
              ></div>
            </div>
          ) : (
            <div
              onClick={GoToCamera}
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                width: "70px",
                height: "70px",
                margin: "10px",
                borderRadius: "10px",
                position: "absolute",
                zIndex: "101",
                bottom: "5%",
                left: "46%",
                cursor: "pointer",
                backgroundColor: "white",
              }}
            >
              <p>다시 촬영</p>
            </div>
          )}
        </div>
      </form>
    </>
  );
};

export default Second;
