import React, { useEffect, useState } from "react";
import Header from "./Header";
import axios from "axios";
import "./Styles/Three.css";
import Slide from "./Slide";
import "./Styles/Slide.css";

const Three = () => {
  const apiUrl = `/recent`;
  const [ImageList, setImageList] = useState([]);
  const [translateX, setTranslateX] = useState(-610);

  // 이미지 받기
  const ImagetoMe = async () => {
    try {
      const { data } = await axios.get(`${apiUrl}`);
      console.log("따끈", data.data.original[0]);

      setImageList(data.data.original[0]);
    } catch (error) {
      console.log(error);
    }
  };

  // 함수 실행할때는 {} 를 꼭 붙여줘야 한다 -> destroy is not function
  useEffect(() => {
    ImagetoMe();
  }, []);

  console.log("??", ImageList);

  let ImgData = ImageList.map((detail, index) => {
    return (
      <>
        <img className="img" src={detail} />
      </>
    );
  });

  //slide
  const slideLeft = (e) => {
    console.log(e.target.classList);
    e.target.classList.toggle("active");
    const slideCount = document.getElementsByClassName("slide").length;
    if (translateX < -610) {
      setTranslateX(translateX + 1220);
    } else {
      setTranslateX(-610 - 1220 * (slideCount - 1));
    }
  };

  return (
    <>
      <Header title={"사진 선택"} />
      <div className="container">
        <div className="selected-container"></div>
        <div className="select-container">
          <div className="img">{ImgData}</div>
        </div>
      </div>
    </>
  );
};

export default Three;
